import React, { useState, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import { X, Search } from 'lucide-react';
import './SearchPanel.css';

const SearchPanel = ({ onClose }) => {
  const [query, setQuery] = useState('');
  const [isExpanded, setIsExpanded] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);
  const textareaRef = useRef(null);

  // Mock data for audience segments
  const mockSegments = [
    { id: 1, name: "Luxury Car Enthusiasts", description: "High-income individuals interested in premium automotive brands", type: "behavioral" },
    { id: 2, name: "Tech Early Adopters", description: "Users who purchase new technology products within the first 90 days", type: "behavioral" },
    { id: 3, name: "Premium Travel Segment", description: "Customers who book first-class flights and luxury accommodations", type: "behavioral" },
    { id: 4, name: "California Millennials", description: "Ages 25-40 living in California with household income $75k+", type: "demographic" },
    { id: 5, name: "Gaming Hardware Buyers", description: "Recent purchasers of gaming laptops, graphics cards, or consoles", type: "purchase" },
    { id: 6, name: "Sustainable Living Advocates", description: "Users interested in eco-friendly products and sustainable brands", type: "interest" }
  ];

  const suggestions = [
    "luxury automotive buyers",
    "tech early adopters california",
    "premium travel customers",
    "gaming enthusiasts",
    "sustainable product buyers"
  ];

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = textareaRef.current.scrollHeight + 'px';
    }
  }, [query]);

  const performSearch = (searchQuery = query) => {
    if (searchQuery.length === 0) {
      setSearchResults([]);
      setShowSuggestions(false);
      setHasSearched(false);
      return;
    }

    setHasSearched(true);
    setShowSuggestions(false);

    // Perform semantic search
    const semanticResults = mockSegments.filter(segment => 
      segment.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
      segment.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setSearchResults(semanticResults.map(s => ({ type: 'segment', ...s })));
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setQuery(value);

    // Show suggestions for short queries (real-time)
    if (value.length > 0 && value.length < 20 && !hasSearched) {
      setShowSuggestions(true);
      // Filter suggestions
      const filteredSuggestions = suggestions.filter(s => 
        s.toLowerCase().includes(value.toLowerCase())
      );
      setSearchResults(filteredSuggestions.map(s => ({ type: 'suggestion', text: s })));
    } else if (value.length === 0) {
      setSearchResults([]);
      setShowSuggestions(false);
      setHasSearched(false);
    }

    // Auto-expand for longer content
    if (value.length > 50 && !isExpanded) {
      setIsExpanded(true);
    }
  };

  const handlePaste = (e) => {
    // Handle RFP paste - typically longer content
    setTimeout(() => {
      if (textareaRef.current.value.length > 100) {
        setIsExpanded(true);
        setShowSuggestions(false);
      }
    }, 0);
  };

  const handleSuggestionClick = (suggestion) => {
    setQuery(suggestion);
    setShowSuggestions(false);
    // Simulate search
    const results = mockSegments.filter(segment => 
      segment.description.toLowerCase().includes(suggestion.toLowerCase())
    );
    setSearchResults(results.map(s => ({ type: 'segment', ...s })));
  };

  return (
    <motion.div
      className="search-panel"
      initial={{ x: '100%' }}
      animate={{ x: 0 }}
      exit={{ x: '100%' }}
      transition={{ 
        type: 'spring',
        damping: 25,
        stiffness: 200,
        duration: 0.5
      }}
    >
      <div className="search-panel-content">
        {/* Header */}
        <div className="search-header">
          <h2>Search Audience Segments</h2>
          <button 
            className="close-button"
            onClick={onClose}
            aria-label="Close search"
          >
            <X size={20} />
          </button>
        </div>

        {/* Search Input */}
        <div className={`search-input-container ${isExpanded ? 'expanded' : ''}`}>
          <div className="search-input-wrapper">
            <Search className="search-icon" size={18} />
            <textarea
              ref={textareaRef}
              className="search-input"
              placeholder="Search audience segments or paste RFP..."
              value={query}
              onChange={handleInputChange}
              onPaste={handlePaste}
              rows={1}
            />
            {isExpanded && (
              <button 
                className="collapse-button"
                onClick={() => setIsExpanded(false)}
                aria-label="Collapse input"
              >
                <X size={16} />
              </button>
            )}
          </div>
        </div>

        {/* Results */}
        <div className="search-results">
          {showSuggestions && searchResults.length > 0 && (
            <div className="suggestions-section">
              <h3>Suggested searches</h3>
              <div className="suggestions-list">
                {searchResults.map((result, index) => (
                  <motion.button
                    key={index}
                    className="suggestion-item"
                    onClick={() => handleSuggestionClick(result.text)}
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.05 }}
                  >
                    <Search size={14} />
                    <span>{result.text}</span>
                  </motion.button>
                ))}
              </div>
            </div>
          )}

          {!showSuggestions && searchResults.length > 0 && (
            <div className="results-section">
              <h3>Search Results</h3>
              <div className="results-list">
                {searchResults.map((result, index) => (
                  <motion.div
                    key={result.id}
                    className="result-item"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <div className="result-header">
                      <h4>{result.name}</h4>
                      <span className={`result-type ${result.type}`}>
                        {result.type}
                      </span>
                    </div>
                    <p className="result-description">{result.description}</p>
                  </motion.div>
                ))}
              </div>
            </div>
          )}

          {query.length > 0 && searchResults.length === 0 && (
            <div className="no-results">
              <p>No segments found. Try adjusting your search terms or paste an RFP for semantic matching.</p>
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default SearchPanel;
